function h = default(type)%DEFAULT Helper function returning common defaults.%if ~exist('type','var')    type = 'number';endswitch lower(type)    case 'headerstring'        h0 = now;        h1 = ['MyMAT-file, saved by MATLAB R' ...                version('-release') ', ' ...                'Platform: ' computer ', '  ...                'Created on: ' datestr(h0, 'ddd') ...                ' ' datestr(h0, 31) ...                ' Magic: ' default('magicstring')];        h = char(default('emptystring') * ...            ones(1, default('headerlength')));        h(1:length(h1)) = h1;    case 'emptystring'        h = char(32);    case 'headerlength'        h = 128;    case 'version'        h = 1;    case 'number'        h = 42;    case 'ext'        h = '.mymat';    case 'name'        h = ['matlab' default('ext')];    case 'format'        %h = 'ieee-be'; % see help fopen        %h = 'ieee-le'; % see help fopen        h = 'native'; % see help fopen    case 'size'        h = 1;    case 'magicstring'        h = 'aDeadCab';    case 'magic'        h = hex2dec(default('magicstring'));    case 'direntrysize'        h = 'uint32';    otherwise        error('No default for that input');endreturn;